import streamlit as st;
import datetime;
import time;

st.set_page_config(layout="wide")

HomeTime = (datetime.datetime.strptime("2022/11/04 15:00:00", "%Y/%m/%d %H:%M:%S") - datetime.datetime.now()).total_seconds();

if "FIT" not in st.session_state:
    st.session_state.FIT = False;
 
if "FIT2" not in st.session_state:
    st.session_state.FIT2 = False;
 
 

while HomeTime > 0 and st.session_state.FIT2 == False:
    # st.text(str(int(HomeTime)) + " ... Counting down the seconds!");
    fullMsg = str(int(HomeTime)) + " ... Counting down the seconds!"
    fullMsg = "<h1 style='text-align: center; color: black;'>" + fullMsg + "</h1>";
    st.markdown(fullMsg, unsafe_allow_html=True)
        
    
    if st.button("Fuck it?"):
        st.session_state.FIT = True;
        
    if st.session_state.FIT == True: 
        if st.button("FUCK IT!"):
            st.session_state.FIT2 = True;
            
